const { Configuration, OpenAIApi } = require("openai");

const configuration = new Configuration({
  apiKey: "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", // Reemplaza con tu clave API
});

const openai = new OpenAIApi(configuration);

async function getResponse(prompt) {
  try {
    const response = await openai.createCompletion({
      model: "text-davinci-003",
      prompt: prompt,
      max_tokens: 100,
    });
    return response.data.choices[0].text.trim();
  } catch (error) {
    console.error("Error al obtener respuesta de OpenAI:", error.response?.data || error.message);
    throw error;
  }
}

module.exports = { getResponse };
